import {signInAction} from "./actions";
import {push} from "connected-react-router"
import {auth, FirebaseTimestamp, db} from "../../firebase/index"

export const signIn = () => {
  return async (dispatch, getState) => {
    // getState()はreduxのメソッド
    //　現在のreduxのstate情報を取ってくる
    const state = getState()
    const isSignedIn = state.users.isSignedIn

    if (!isSignedIn) {
      const url ="https://api.github.com/users/ShojiroAbe"

      const response = await fetch(url)
                      .then(res=>res.json())
                      .catch(()=>null)

      const username = response.login

      dispatch(signInAction({
        isSignedIn: true,
        uid: "00001",
        username: username
      }))
      dispatch(push("/"))
    }
  }
}

export const signUp = (username, email, password, confirmPassword) => {
  return async (dispatch) => {
    // Validation
    if (username === "" || email === "" || password === "" || confirmPassword === ""){
      alert("必須項目が未入力です")
      return false
    }

    if (password !== confirmPassword) {
      alert("パスワードが一致しません。もう一度お試しください。")
      return false
    }

    // createUserWithEmailAndPasswordはfirebaseのアカウントを作るメソッド
    return auth.createUserWithEmailAndPassword(email, password)
    .then(result => {
      const user = result.user

      if(user){
        const uid = user.uid
        const timestamp = FirebaseTimestamp.now()

        const userInitialDate = {
          created_at: timestamp,
          email: email,
          role: "customer",
          uid: uid,
          updated_at: timestamp,
          username: username
        }
        // createUserWithEmailAndPasswordで作られたアカウント情報を登録する（データベースに登録）
        db.collection("users").doc(uid).set(userInitialDate)
          .then(() => {
            // 登録が完了したらホームへ行く処理
            dispatch(push("/"))
          })
      }
    })
  }
}
